function Number({ n }) {
  const [ref, inView] = useInViewObserve({
    triggerOnce: true,
  });

  const { number } = useSpring({
    from: { number: 0 },
    number: inView ? n : 0,
    delay: 200,
    config: { mass: 1, tension: 20, friction: 10 },
  });

  return (
    <animated.div ref={ref}>{number.to((n) => n.toFixed(0))}</animated.div>
  );
}

